<script type="application/javascript">
    var $ = jQuery;

    $('.copy').on('click', function(){
        var copyText = $(this).prev('input');
        copyText.select();
        document.execCommand("Copy");
        $(this).next('div').fadeIn();
        $(this).next('div').fadeOut(1000);
    });

    function getNewApiKeys() {
        $.ajax({
            method: 'POST',
            dataType: 'json',
            url: '<?php echo admin_url('admin-ajax.php'); ?>',
            data: {
                action: 'woocommerce_update_api_key',
                security: '<?php echo wp_create_nonce('update-api-key'); ?>',
                key_id: 0,
                description: "CONVERSIFIC_API_CONNECT",
                user: 1,
                permissions: "read"
            },
            success: function (response) {
                if (response.success) {
                    var data = response.data;
                    var $key = $('#consumer_key');
                    var $secret = $('#consumer_secret');
                    $(".conv_custom_message").fadeOut();

                    if (0 < data.consumer_key.length && 0 < data.consumer_secret.length) {
                        $key.val(data.consumer_key);
                        $secret.val(data.consumer_secret);
                        $(".hidden-fields").fadeIn();
                        $("#noticeText").text(data.message);
                        $("#alert").show();
                        $("#generate-buttons").fadeOut();
                    } else {
                        $("#noticeText").text(data.message);
                        $("#alert").show();
                    }
                } else {
                    $("#noticeText").text(response.data.message);
                    $("#alert").show();
                }
            }
        });
    }

    $('#generate').on("click", function () {
        if ($('#override-api-keys').is(':checked')) {
            $.ajax({
                method: 'GET',
                url: '<?php echo admin_url('admin-ajax.php'); ?>',
                data: {
                    action: 'conversific_remove_api_keys'
                },
                success: function () {
                    getNewApiKeys();
                }
            });
        } else {
            getNewApiKeys();
        }
    });
</script>
